import styles from './Login.module.scss';
import Image from 'components/Image/Image';
import {useState} from 'react';
import {NavLink} from 'react-router-dom';

const Login = () => {
  const [username, setUserName] = useState<string>('');
  const [password, setPassword] = useState<string>('');
  const [errorStatus, setErrorStatus] = useState<boolean>(false);
  const [errorMessage, setErrorMessage] = useState<string>('');

  //   setError = () => {
  //     let username = document.getElementById('username_input')
  //     let password = document.getElementById('password_input')
  //     let errorText = ''

  //     if (username.value === '' && password.value === '') {
  //       errorText = 'Please enter a valid username and password.'
  //     } else if (username.value === '') {
  //       errorText = 'Please enter a valid username.'
  //     } else if (password.value === '') {
  //       errorText = 'Please enter your password.'
  //     }
  //     return errorText
  //   }

  const errorCheck = (): void => {
    const message = 'Please enter a valid username and password.';

    //empty credentials error
    if (username === '' || password === '') {
      setErrorStatus(true);
      setErrorMessage(message);
    } else {
      setErrorStatus(false);
      setErrorMessage('');
    }
  };

  const login = () => {
    console.log;
    errorCheck();
  };

  return (
    <section className={styles.LoginContainer}>
      <Image />
      <section className={styles.LoginForm}>
        <h2 className={styles.FormHeaderCopy}>Login</h2>
        <section>
          <input type='text' placeholder='Username' value={username} onChange={(e) => setUserName(e.target.value)} />
          <input type='text' placeholder='Password' value={password} onChange={(e) => setPassword(e.target.value)} />
        </section>
        {errorStatus && <p className={styles.Error}>{errorMessage}</p>}
        <button className={styles.LoginButton} onClick={() => login()}>
          Login
        </button>
        <p className={styles.ForgotPasswordCopy}>Forgot Password</p>
        <section className={styles.CreateAccount}>
          <p>New to BGM?</p>
          <p>
            <NavLink to='/signup'>Create an account.</NavLink>
          </p>
        </section>
      </section>
    </section>
  );
};

export default Login;
